from helperfunctions import sequence2string, LongestMatches, colour_matches, homology_output

#####################
# GATHERING SEQUENCES
#####################

"""
ADCY1, HUMAN, TRANSCRIPT VARIANT 1
Transcript length = 12 499
Default taqman probe: Hs00299832_m1
Assay location: 1041
Amplicon length: 56

FASTA link: https://www.ncbi.nlm.nih.gov/nuccore/NM_021116.2
"""

AC1_human_TV1 = sequence2string('')

"""
ADCY1, HUMAN, TRANSCRIPT VARIANT 2
Transcript length = 1763
Default taqman probe: same as for TV1
Assay location: 727
Amplicon length: 56

FASTA link: https://www.ncbi.nlm.nih.gov/nuccore/NM_001281768.1
"""

AC1_human_TV2 = sequence2string('')

"""
ADCY1, MOUSE
Transcript length = 12 259
Default taqman probe: Mm01187829_m1
Assay location: 3166
Amplicon length: 66

FASTA link: https://www.ncbi.nlm.nih.gov/nuccore/NM_009622.1?report=fasta
"""

AC1_mouse = sequence2string('')

"""
ADCY1, RAT
Transcript length = 3219
Default taqman probe: Rn02115682_s1
Assay location: 3137
Amplicon length: 131

FASTA link: https://www.ncbi.nlm.nih.gov/nuccore/NM_001107239.1?report=fasta 
"""

AC1_rat = sequence2string('')

"""
ADCY1, SHEEP X2
Transcript length = 2419
FASTA link: https://www.ncbi.nlm.nih.gov/nuccore/XM_042248694.1?report=fasta
"""

AC1_sheep_X2 = sequence2string('')

"""
ADCY1, SHEEP X1
Transcript length = 11 303
FASTA link: https://www.ncbi.nlm.nih.gov/nuccore/XM_027968689.2?report=fasta
"""

AC1_sheep_X1 = sequence2string('')

##############################################
# COMPUTE PAIR-WISE COMPARISONS OF TRANSCRIPTS
##############################################

"""
MOUSE vs. SHEEP X1 (long transcript)
"""

list_matches_mouse_sheepX1 = LongestMatches(AC1_mouse, AC1_sheep_X1)
print("There is " + str(len(list_matches_mouse_sheepX1)) + " regions of homology >= 19nt in mouse & sheep X1 (long) ADCY1.")

coloured_mouse_sheepX1 = colour_matches(AC1_mouse,list_matches_mouse_sheepX1)
coloured_sheepX1_mouse = colour_matches(AC1_human_TV1,list_matches_mouse_sheepX1)

"""
HUMAN TV1 (long transcript) vs. SHEEP X1 (long transcript)
"""

list_matches_humanTV1_sheepX1 = LongestMatches(AC1_human_TV1, AC1_sheep_X1)
print("There is " + str(len(list_matches_humanTV1_sheepX1)) + " regions of homology >= 19nt in human TV1 & sheep X1 (long) ADCY1.")

coloured_sheepX1_humanTV1 = colour_matches(AC1_sheep_X1,list_matches_humanTV1_sheepX1)
coloured_humanTV1_sheepX1 = colour_matches(AC1_human_TV1,list_matches_humanTV1_sheepX1)

"""
MOUSE vs. human TV1 (long transcript)
"""
list_matches_mouse_tv1 = LongestMatches(AC1_mouse, AC1_human_TV1)
print("There is " + str(len(list_matches_mouse_tv1)) + " regions of homology >= 19nt in mouse & human TV1 (long) ADCY1.")

coloured_mouse_tv1 = colour_matches(AC1_mouse,list_matches_mouse_tv1)
coloured_tv1_mouse = colour_matches(AC1_human_TV1,list_matches_mouse_tv1)

"""
RAT vs. MOUSE
"""
list_matches_rat_mouse = LongestMatches(AC1_rat, AC1_mouse)
print("There is " + str(len(list_matches_rat_mouse)) + " regions of homology >= 19nt in rat & mouse ADCY1.")

coloured_mouse_rat = colour_matches(AC1_mouse,list_matches_rat_mouse)
coloured_rat_mouse = colour_matches(AC1_rat,list_matches_rat_mouse)

"""
RAT vs. human TV1 (long transcript)
"""
list_matches_rat_tv1 = LongestMatches(AC1_rat, AC1_human_TV1)
print("There is " + str(len(list_matches_rat_tv1)) + " regions of homology >= 19nt in rat & human TV1 (long) ADCY1.")

coloured_rat_tv1 = colour_matches(AC1_rat,list_matches_rat_tv1)
coloured_tv1_rat = colour_matches(AC1_human_TV1,list_matches_rat_tv1)

"""
RAT vs. human TV2 (short transcript)
"""
list_matches_rat_tv2 = LongestMatches(AC1_rat, AC1_human_TV2)
print("There is " + str(len(list_matches_rat_tv2)) + " regions of homology >= 19nt in rat & human TV2 (short) ADCY1.")

coloured_rat_tv2 = colour_matches(AC1_rat,list_matches_rat_tv2)
coloured_tv2_rat = colour_matches(AC1_human_TV2,list_matches_rat_tv2)

"""
HUMAN TV1 vs. TV2
"""
list_matches_tv1_tv2 = LongestMatches(AC1_human_TV1, AC1_human_TV2)
print("There is " + str(len(list_matches_tv1_tv2)) + " regions of homology >= 19nt in human tv1 & tv2 ADCY1.")

coloured_tv1_tv2 = colour_matches(AC1_human_TV1,list_matches_tv1_tv2)
coloured_tv2_tv1 = colour_matches(AC1_human_TV2,list_matches_tv1_tv2)

############################################################
# OUTPUT THE DESIRED SEQUENCE WITH HOMOLOGOUS REGIONS MARKED
############################################################

homology_output(coloured_mouse_tv1,coloured_tv1_mouse,coloured_mouse_rat,coloured_rat_mouse,
                    coloured_rat_tv1,coloured_tv1_rat,coloured_rat_tv2,coloured_tv2_rat,
                    coloured_tv1_tv2, coloured_tv2_tv1)


